mapper.select:
select

from xd_goods_pos
where good_id = #{goodId,jdbcType=BIGINT} AND capacity = #{capacity,jdbcType=INTEGER}

mapper.update:
update xd_goods_pos
set good_id = #{goodId,jdbcType=BIGINT},
  capacity = #{capacity,jdbcType=INTEGER},
  utc_create = #{utcCreate,jdbcType=TIMESTAMP},
  utc_modified = #{utcModified,jdbcType=TIMESTAMP}
where goods_pos_id = #{goodsPosId,jdbcType=BIGINT}

update xd_goods_pos
set capacity = #{capacity,jdbcType=INTEGER},
  utc_create = #{utcCreate,jdbcType=TIMESTAMP},
  utc_modified = #{utcModified,jdbcType=TIMESTAMP}
where good_id = #{goodId,jdbcType=BIGINT}

update xd_goods_pos
set capacity = #{capacity,jdbcType=INTEGER}
where goods_pos_id = #{goodsPosId,jdbcType=BIGINT}
